var settings = flow.get("settings");
settings.confidence = parseInt(msg.payload);
flow.set("settings", settings);
return msg;

var settings = flow.get("settings");
if(!settings)
    return;

var list = [];

msg.payload.forEach( function(item){
    if (item.confidence < settings.confidence)
        return;
    if( item.age < settings.age)
        return;
    var dx = (item.cx - item.bx);
    dx = dx * dx;
    var dy = (item.cy - item.by);
    dy = dy * dy;
    item.distance = parseInt(Math.sqrt(dx+dy)/10);
    if( item.distance < settings.distance )
        return;

    var clasMatch = false;
    settings.classes.forEach(function (element) {
        if (element === item.class)
            clasMatch = true;
    });
    if (!clasMatch)
        return;

    if( item.cx < settings.aoi.x1 )
        return;
    if (item.cx > settings.aoi.x2)
        return;
    if (item.cy < settings.aoi.y1)
        return;
    if (item.cy > settings.aoi.y2)
        return;
    list.push(item);
});


msg.payload = list;

return msg;

msg.payload = {
    classes: ["Human", "Car"],
    age: 2,
    distance: 10,
    confidence: 30,
    aoi: {
        x1: 200,
        y1: 200,
        x2: 800,
        y2: 800
    },
    timeout: 5
}

var device = global.get("device");
if( device ) 
    msg.payload.name = device.hostname || device.serial;

return msg;

if( msg.payload.length === 0 )
    return;
msg.topic = "state";
var settings = flow.get("settings");
msg.delay = settings.timeout * 1000;
return msg;

msg.topic = "init";
msg.payload = {
    aspect: "16:9",
    rotation: 0,
    resolution: "1280x720"
}

var camera = global.get("camera");
if ( camera ) {
    msg.payload.aspect = camera.aspect;
    msg.payload.rotation = camera.rotation;
    msg.payload.resolution = camera.largest;
}

return msg;
